class MockWorldServerSession:
    def __init__(self, session_key):
        self.session_key = session_key
        self.guid = None

    async def handle(self, stream):
        protocol = WorldServerStream(stream, session_key=self.session_key)

        # Transfer session from auth server
        await protocol.send_unencrypted_packet(
            SMSG_AUTH_CHALLENGE,
            make_SMSG_AUTH_CHALLENGE(
                server_seed=7, encryption_seed1=31, encryption_seed2=71
            ),
        )

        auth_session = await protocol.receive_unencrypted_packet(CMSG_AUTH_SESSION)
        await protocol.send_unencrypted_packet(
            SMSG_AUTH_RESPONSE,
            make_SMSG_AUTH_RESPONSE(AuthResponse.ok, Expansion.wotlk),
        )

